number_to_currency = function(value, precision, currency) {
  if (precision == null) {
    precision = 0;
  }
  if (currency == null) {
    currency = '';
  }
  if ($.isNumeric(value)) {
    return $.toCurrency(value, {
      precision: precision,
      delimiter: ",",
      separator: ".",
      unit: currency,
      format: "%u%n",
      negativeFormat: "-%u%n"
    });
  }
};
